let audio=document.getElementById("audio"),playPauseButton=document.getElementById("play-pause"),seekBar=document.getElementById("seek-bar"),currentTime=document.getElementById("current-time"),duration=document.getElementById("duration");function formatTime(e){var t=Math.floor(e/60),e=Math.floor(e%60);return t+":"+(e<10?"0":"")+e}console.log(playPauseButton),audio.addEventListener("timeupdate",()=>{var e=audio.currentTime/audio.duration*100;seekBar.value=e,currentTime.textContent=formatTime(audio.currentTime)}),audio.addEventListener("loadedmetadata",()=>{duration.textContent=formatTime(audio.duration)}),playPauseButton.addEventListener("click",()=>{audio.paused?(audio.play(),playPauseButton.textContent="Pause"):(audio.pause(),playPauseButton.textContent="Play")}),seekBar.addEventListener("input",()=>{var e=seekBar.value/100*audio.duration;audio.currentTime=e});